import {
	START_VIDEO,
	VIDEO_ACCEPTED,
	VIDEO_MISSED,
	VIDEO_CLOSED,
	VIDEO_CALLING
} from "redux/constants/videoCall";

import { VIDEO_CALL_CALLING } from "redux/constants/videoCall";

import { RESET } from "redux/constants/main";

const initialState = {
	calling: false,
	initiateCall: false
};

export default function messageReducer(state = initialState, action) {
	switch (action.type) {
		case START_VIDEO:
			return Object.assign({}, state, {
				initiateCall: true
			});

		case VIDEO_CALL_CALLING:
			return Object.assign({}, state, {
				calling: true
			});

		case VIDEO_ACCEPTED:
			return Object.assign({}, state, {
				calling: false
			});

		case VIDEO_MISSED:
			return Object.assign({}, state, {
				calling: false
			});

		case VIDEO_CLOSED:
			return Object.assign({}, state, {
				calling: false
			});

		case RESET:
			return initialState;

		default:
			return state;
	}
}
